function ucapan() {
	const time = moment.tz("Asia/Jakarta").format("HH");
	let res = "Sudah Dini Hari Kok Belum Tidur Kak?";
	if (time >= 4) {
		res = "sᴇʟᴀᴍᴀᴛ ᴘᴀɢɪ";
	}
	if (time >= 10) {
		res = "sᴇʟᴀᴍᴀᴛ sɪᴀɴɢ";
	}
	if (time >= 15) {
		res = "sᴇʟᴀᴍᴀᴛ sᴏʀᴇ";
	}
	if (time >= 18) {
		res = "sᴇʟᴀᴍᴀᴛ ᴍᴀʟᴀᴍ";
	}
	return res;
}
